import React from "react";
import styled from "styled-components";

const Section = styled.section` 
min-height: 100vh;
height: auto;
width: 100vw;
margin 0 auto;
overflow: hidden;

display: flex;
justfiy-content: flex-start;
align-items: flex-start;

position: relative;

`;

const Title = styled.h1`
	font-size: ${(props) => props.theme.fontxxxl};
	font-family: "kaushan Script";
	font-weight: 300;

	color: ${(props) => props.theme.text};

	position: absolute;
	top: 1rem;
	left: 5%;
	z-index: 11;
`;

// const Left = styled.div`
// 	width: 85%;
// 	background-color: ${(props) => props.theme.body};
// 	color: ${(props) => props.theme.text};

// 	min-height: 1000vh;
// 	z-index: 5;
// `;

const Left = styled.div`
	width: 35%;
	background-color: ${(props) => props.theme.body};
	color: ${(props) => props.theme.text};

	min-height: 1000vh;
	z-index: 5;
`;

const Shop = () => {
	return (
		<Section>
			<Title data-scroll data-scroll-speed="-1">
				New Arrivals
			</Title>
			<Left>
				<p>
					The brand new collection is currently being developed in USA. We
					create our products using best quality material, including the use of
					some of the pure fabrics to make our products. All products are made
					using the best materials, from the finest cotton to the finest
					fabrics. We have lots of different clothing options like shoes,
					jackets and dresses. Not only clothes but we also provide unique
					Jewellery as well. It is great for us to carry our new clothes all
					around the country and look different.
				</p>
			</Left>
		</Section>
	);
};

export default Shop;
