import React from "react";
import MainVideo from "../assets/Walking Girl.mp4";
import styled from "styled-components";
import { motion } from "framer-motion";

const VideoContainer = styled.section`
	width: 100%;
	height: 100vh;
	position: relative;

	video {
		width: 100%;
		height: 100vh;
		object-fit: cover;

		@media (max-width: 48em) {
			object-position: center 10%;
		}
	}

	@media (max-width: 30eem) {
		object-position: center 50%;
	}
`;

const DarkOverlay = styled.div`
	position: absolute;
	top: 0;
	bottom: 0;
	left: 0;
	right: 0;
	z-index: 1;

	background-color: ${(props) => `rgba(${props.theme.bodyRgba}, 0.3)`};
`;

const Title = styled(motion.div)`
	position: absolute;
	top: 0;
	bottom: 0;
	left: 0;
	right: 0;
	z-index: 5;

	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	color: ${(props) => props.theme.text};

	div {
		display: flex;
		flex-direction: row;
	}

	h1 {
		font-family: "kaushan Script";
		font-size: ${(props) => props.theme.fontBig};
		text-shadow: 1px 1px 1px ${(props) => props.theme.body};

		@media (max-width: 30em) {
			font-size: calc(5rem + 8vw);
		}
	}

	h2 {
		font-family: "Sirin Stencil";
		font-size: ${(props) => props.theme.fontlg};
		text-shadow: 1px 1px 1px ${(props) => props.theme.body};

		font-weight: 300;

		text-transform: capitalize;

		@media (max-width: 30em) {
			font-size: calc(5rem + 8vw);
		}
	}
`;

const container = {
	hidden: {
		opacity: 0,
	},
	show: {
		opacity: 1,

		transition: {
			delayChildren: 5, //2
			staggerChildren: 0.3,
		},
	},
};

const item = {
	hidden: {
		opacity: 0,
	},
	show: {
		opacity: 1,
	},
};

const CoverVideo = () => {
	return (
		<VideoContainer>
			<DarkOverlay />
			<Title variants={container} initial="hidden" animate="show">
				<div>
					<motion.h1
						variants={item}
						data-scroll
						data-scroll-delay="0.13"
						data-scroll-speed="4"
					>
						V{" "}
					</motion.h1>
					<motion.h1
						variants={item}
						data-scroll
						data-scroll-delay="0.09"
						data-scroll-speed="4"
					>
						I{" "}
					</motion.h1>
					<motion.h1
						variants={item}
						data-scroll
						data-scroll-delay="0.06"
						data-scroll-speed="4"
					>
						B{" "}
					</motion.h1>
					<motion.h1
						variants={item}
						data-scroll
						data-scroll-delay="0.04"
						data-scroll-speed="4"
					>
						E{" "}
					</motion.h1>
				</div>
				<motion.h2
					variants={item}
					data-scroll
					data-scroll-delay="0.04"
					data-scroll-speed="2"
				>
					Inspire. Create. Believe
				</motion.h2>
			</Title>
			<video src={MainVideo} type="video/mp4" autoPlay muted loop></video>
		</VideoContainer>
	);
};

export default CoverVideo;
