import React from "react";
import MainVideo from "../assets/Walking Girl.mp4";
import styled from "styled-components";
import { motion } from "framer-motion";

const VideoContainer = styled.section`
	width: 100%;
	height: 100vh;
	position: relative;

	video {
		width: 100%;
		height: 100vh;
		object-fit: cover;
	}
`;

const DarkOverlay = styled.div`
	position: absolute;
	top: 0;
	bottom: 0;
	left: 0;
	right: 0;
	z-index: 1;

	background-color: ${(props) => `rgba(${props.theme.bodyRgba}, 0.3)`};
`;

const Title = styled.div`
	position: absolute;
	top: 0;
	bottom: 0;
	left: 0;
	right: 0;
	z-index: 5;

	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	color: ${(props) => props.theme.text};

	div {
		display: flex;
		flex-direction: row;
	}

	h1 {
		font-family: "kaushan Script";
		font-size: ${(props) => props.theme.fontBig};
		text-shadow: 1px 1px 1px ${(props) => props.theme.body};
	}

	h2 {
		font-family: "Sirin Stencil";
		font-size: ${(props) => props.theme.fontlg};
		text-shadow: 1px 1px 1px ${(props) => props.theme.body};

		font-weight: 300;

		text-transform: capitalize;
	}
`;

const container = {
	hidden: {
		opacity: 0,
	},
	visible: {
		opacity: 1,

		transition: {
			duration: 2,
			ease: "easeInOut",
		},
	},
};

const CoverVideo = () => {
	return (
		<VideoContainer>
			<DarkOverlay />
			<Title>
				<div>
					<h1 data-scroll data-scroll-delay="0.13" data-scroll-speed="4">
						V{" "}
					</h1>
					<h1 data-scroll data-scroll-delay="0.09" data-scroll-speed="4">
						I{" "}
					</h1>
					<h1 data-scroll data-scroll-delay="0.06" data-scroll-speed="4">
						B{" "}
					</h1>
					<h1 data-scroll data-scroll-delay="0.04" data-scroll-speed="4">
						E{" "}
					</h1>
				</div>
				<h2 data-scroll data-scroll-delay="0.04" data-scroll-speed="2">
					Inspire. Create. Believe
				</h2>
			</Title>
			<video src={MainVideo} type="video/mp4" autoPlay muted loop></video>
		</VideoContainer>
	);
};

export default CoverVideo;
